import {useEffect, useState } from "react";
import Chart from "chart.js/auto";
import './header.css'
import {Bar, Doughnut, Pie} from 'react-chartjs-2'


function Sales(props){
    const [duration, setDuration] = useState(365);
    const [d, setd] = useState([]);
    const [chart_dataa, set_chart_data] = useState();

    //predefined options (settings) for bar charts
    const barOptions = {
        responsive: true,
        plugins: {
            legend: {
            position: 'top',
            },
            title: {
            display: true,
            }
        },
    }

    function handleResponse(response){
        var dArray = []
        let labels = response['name']
        const chart_data = {
            labels,
            datasets: [{
                data: response['count'],
                backgroundColor: 'rgba(255, 99, 132, 0.5)',
            }],
        }
        dArray.push(<Bar options={chart_options} data={chart_data} />)
        setd(dArray)
    }

    useEffect(() =>{
        fetch('http://127.0.0.1:5000/auto_reports',{
            method: 'GET',
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(res => res.json())
        .then(data => {handleResponse(data)})
    }, [duration])

    return (
        <div style={{width: '50%', margin: 'auto'}}>
        <Header handleInput={handleInput}/>
        {d}
        </div>
    )
}

function Header(props){

    return (
        <div className='pageHeader'>
            <div className="pageTitle">Auto Generated Reports</div>
            {/* <div className="pageHeaderInner">
                <select name="timeInput" id="timeInput" onChange={props.handleInput}>
                    <option value="365">Last 1 Year</option>
                    <option value='180'>Last 180 Days</option>
                    <option value='90'>Last 90 Days</option>
                    <option value='30'>Last 30 Days</option>
                </select>
            </div> */}
        </div>
    )

}

export default Sales;
